import React, { useState } from 'react';

interface Props {
  onUpload: (artifact: AAHEnvelope) => void;
  projectId?: string;
}

export function ArtifactUploader({ onUpload, projectId }: Props) {
  const [uploading, setUploading] = useState(false);

  const handleSubmit = async (file: File) => {
    setUploading(true);
    try {
      const content = await file.text();
      const envelope = createAAHEnvelope({
        type: 'document/markdown',
        title: file.name,
        content,
        projectId,
      });
      await onUpload(envelope);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="upload-zone">
      {/* Upload UI */}
    </div>
  );
}
